import { compile } from 'handlebars';

const upsellTemplate = `
<div id="CartUpsaleFormContainer" class="drawer__footer appear-animation appear-delay-5">
  <div class="add_more_pair">
    <p class="save_eighty text-center">{{ upsellHeading }}</p>
    <p class="text-center">{{ upsellSubheading }}</p>
  </div>
  <div class="inCartUpsaleProduct--container">
    {{#upsellProducts}}
      <div class="inCartUpsaleProduct">
        <img class="inCartUpsaleProduct--img" src="{{featured_image}}" alt="{{title}}">
        <p class="inCartUpsaleProduct--title">{{title}} - {{price}}</p>
        <div class="inCartUpsaleProduct--form">
          <select class="inCartUpsaleProduct--variantSelect" data-product-id="{{id}}">
            {{#variants}}
              {{#if available  }}
                {{#if_eq ../../preselectedVariation title}}
                  <option value="{{id}}" selected>{{ title }}</option>
                {{else}}
                  <option value="{{id}}">{{ title }}</option>
                {{/if_eq}}
              {{/if }}
            {{/variants}}
          </select>
          <button type="button" data-product-id="{{id}}" class="inCartUpsaleProduct--submitBtn btn-white btn--full">Add</button>
        </div>
      </div>
    {{/upsellProducts}}
  </div>
  <div class="add-more-pair-shop-now">
    <a href="{{ browseMoreUrl }}" class="shop-now-btn">{{ browseMoreUrlButtonText }}</a>
  </div>
</div>
`

export default function generateUpsellSnippet(upsellProducts = []) {
  const snippet = compile(upsellTemplate, {
    upsellHeading: 'Lorem ipsum',
    upsellSubheading: 'Lorem ipsum',
    upsellProducts: [],
    browseMoreUrl: '',
    browseMoreUrlButtonText: 'Lorem ipsum'
  })
  return snippet;
}
