const axios = require('axios');

export default function handler(req, res) {
  const url = `https://rens-disposable.myshopify.com`;
  const themePath = `admin/api/2020-07/themes.json`;
  const accessToken = req.cookies.accessToken;
  axios.get(`${url}/${themePath}`, {
    headers: {
      'X-Shopify-Access-Token': accessToken
    }
  }).then(({ data }) => {
    const themes = data.themes;
    console.log({ themes });
  })
  res.status(200).json([])
}
